			Add("pt", "WelcomeToTheGame", @"<p>Bem-vindo ao <b>Orion`s Belt</b>! Neste momento estás sozinho na tua <a href=" + '"' + @"Manual.aspx?path=Universe/PrivateZone.aspx" + '"' + @">Zona Privada</a> com
